      panorama7, 
      viewer,
      progress, 
      progressElement;

  function onEnter ( event ) {
    progressElement.style.width = 0;
    progressElement.classList.remove( 'finish' );
  }

  function onProgress ( event ) {
    progress = event.progress.loaded / event.progress.total * 100;
    progressElement.style.width = progress + '%';
    if ( progress === 100 ) {
      progressElement.classList.add( 'finish' );
    }
  }

  // Create spheres
  panorama1 = new PANOLENS.ImagePanorama(one);
  panorama2 = new PANOLENS.ImagePanorama(two);
  panorama3 = new PANOLENS.ImagePanorama(three);
  panorama4 = new PANOLENS.ImagePanorama(four);
  panorama5 = new PANOLENS.ImagePanorama(five);
  panorama6 = new PANOLENS.ImagePanorama(six);
  panorama7 = new PANOLENS.ImagePanorama(seven);

  // Add progress listeners to panoramas
  panorama1.addEventListener( 'progress', onProgress );
  panorama1.addEventListener( 'enter', onEnter );
  panorama2.addEventListener( 'progress', onProgress );
  panorama2.addEventListener( 'enter', onEnter );
  panorama3.addEventListener( 'progress', onProgress );
  panorama3.addEventListener( 'enter', onEnter );
  panorama4.addEventListener( 'progress', onProgress );
  panorama4.addEventListener( 'enter', onEnter );
  panorama5.addEventListener( 'progress', onProgress );
  panorama5.addEventListener( 'enter', onEnter );
  panorama6.addEventListener( 'progress', onProgress );
  panorama6.addEventListener( 'enter', onEnter );
  panorama7.addEventListener( 'progress', onProgress );
  panorama7.addEventListener( 'enter', onEnter );

  // Link spheres
  panorama1.link(panorama2, new THREE.Vector3(-207.5, 504.88, -6000.0));
  panorama2.link(panorama3, new THREE.Vector3(-6907.5, 304.88, 1000.0));
  panorama3.link(panorama4, new THREE.Vector3(-207.5, 504.88, -6000.0));
  panorama4.link(panorama5, new THREE.Vector3(-207.5, 504.88, -6000.0));
  panorama5.link(panorama6, new THREE.Vector3(-207.5, 504.88, -6000.0));
