/* eslint-disable no-unused-vars */
import React, { useEffect, useState, useRef } from "react";
import * as PANOLENS from "panolens";
import * as THREE from "three";
import ReactHowler from "react-howler";
import GifLoader from "three-gif-loader";
import Typed from "typed.js";
// Spheres
import one from "../assets/spheres/one.jpg";
import two from "../assets/spheres/two.jpg";
import three from "../assets/spheres/three.jpg";
import four from "../assets/spheres/four.jpg";
import five from "../assets/spheres/five.jpg";
import six from "../assets/spheres/six.jpg";
import seven from "../assets/spheres/seven.jpg";
// Artworks
import imagePlaceholder from "../assets/art/artworkPlaceholder.jpg";
import gifPlaceholder from "../assets/gifs/gifPlaceholder.gif";
// Audio
import audio from "../assets/audio/audio.mp3"


export default function Guided() {
  const [progressEl, setProgessEl] = useState(null);
  const spanEl = useRef(null);

  var panorama1,
      panorama2,
      panorama3,
      panorama4,
      panorama5,
      panorama6,
      panorama7,
      viewer,
      typed;

  var sphereOneArt,
    valveInfospot,
    seatInfospot,
    topboxInfospot,
    endingInfospot;

  useEffect(() => {
    setProgessEl(document.querySelector("#progress"));
  }, [setProgessEl]);

  var tweeningDelay = 300;
  // var tweeningDelay = 300,
  // typeStartDelay = 1000,
  // typeSpeed = 50;

  var paragraphs = {
    welcome: [
      "Hello and welcome to your guided tour of The Advantages of Tender Loving Care. Thank you for joining me!",
      "We are currently on Toronto Island,",
    ],
    sphereOnePainting: [
      "This is called artwork title and is a blank blank created by blank and including blank.",
    ],
    valve: [
      "A Valve",
      "solid looking and quite decent color",
      "here shows the evidence that time would leaves marks on everything",
      "same thing applies to our face...",
    ],
    seat: [
      "Alright. Here we have a seat. The green pad looks quite comfortable",
      "when you are on a train",
      "OK. Move on to next one",
    ],
    box: [
      "There is a box over there",
      "probably some fuse or electronic devices",
      "Hmmmm............",
      "Anyway",
      "Let's keep moving",
    ],
    ending: [
      "This is our last stop",
      "Please continue your journey with this wonder train...",
    ],
  };

  // Patch for typed.js of cutting back-to-back words
  for (var section in paragraphs) {
    if (paragraphs.hasOwnProperty(section)) {
      paragraphs[section].unshift("");
      paragraphs[section].push("");
    }
  }

  function delayExecute(func, delay) {
    setTimeout(func, delay);
  }

  function onLoad() {
    const options = {
      strings: paragraphs.welcome,
      typeSpeed: 50,
      backSpeed: 50,
      showCursor: false,
    };
    if (spanEl.current) {
      typed = new Typed(spanEl.current, options);
    }
    setTimeout(() => {
      onWelcomeComplete();
    }, 16000);
  }

  function typeItUp(strings) {
    const options = {
      strings: strings,
      typeSpeed: 50,
      backSpeed: 50,
      showCursor: false,
    };
    if (spanEl.current) {
      typed = new Typed(spanEl.current, options);
    }
  }

  function onEnter(event) {
    if (progressEl) {
      progressEl.style.width = 0;
      progressEl.classList.remove("finish");
    }
  }

  function onProgress(event) {
    var progress = (event.progress.loaded / event.progress.total) * 100;
    if (progressEl) {
      progressEl.style.width = progress + "%";
      if (progress === 100) {
        progressEl.classList.add("finish");
      }
    }
  }

  function onWelcomeComplete() {
    delayExecute(sphereOneArt.focus.bind(sphereOneArt, 20000), tweeningDelay);
    setTimeout(() => {
      onMeterTourComplete();
    }, 6000);
  }

  function onMeterTourComplete() {
    typeItUp(paragraphs.sphereOnePainting)
    // delayExecute(valveInfospot.focus.bind(valveInfospot), tweeningDelay);
    // setTimeout(() => {
    //   onValveTourComplete();
    // }, 5000);
  }

  function onValveTourComplete() {
    delayExecute(seatInfospot.focus.bind(seatInfospot), tweeningDelay);
    setTimeout(() => {
      onSeatTourComplete();
    }, 4000);
  }

  function onSeatTourComplete() {
    delayExecute(topboxInfospot.focus.bind(topboxInfospot), tweeningDelay);
    setTimeout(() => {
      onTopboxTourComplete();
    }, 4000);
  }

  function onTopboxTourComplete() {
    delayExecute(endingInfospot.focus.bind(endingInfospot), tweeningDelay);
    setTimeout(() => {
      // viewer.OrbitControls.enabled = true;
      viewer.setPanorama(panorama2);
    }, 5000);
  }

  // Infospots
  sphereOneArt = new PANOLENS.Infospot(10e-7);
  valveInfospot = new PANOLENS.Infospot();
  seatInfospot = new PANOLENS.Infospot();
  topboxInfospot = new PANOLENS.Infospot();
  endingInfospot = new PANOLENS.Infospot(10e-7); // Make it not visible

  sphereOneArt.position.set(-30, 100, 340);
  valveInfospot.position.set(4637.61, -798.12, -1671.24);
  seatInfospot.position.set(1934.61, -2611.69, -3792.91);
  topboxInfospot.position.set(-3348.82, 3705.92, 45.54);
  endingInfospot.position.set(-3461.4, -3592.37, -241.38);

  // sphereOneArt.addHoverText("Speedometer", 50);
  // valveInfospot.addHoverText("Valve", 50);
  // seatInfospot.addHoverText("Seat", 50);
  // topboxInfospot.addHoverText("Box", 50);

  // Create spheres
  panorama1 = new PANOLENS.ImagePanorama(one);
  panorama2 = new PANOLENS.ImagePanorama(two);
  panorama3 = new PANOLENS.ImagePanorama(three);
  panorama4 = new PANOLENS.ImagePanorama(four);
  panorama5 = new PANOLENS.ImagePanorama(five);
  panorama6 = new PANOLENS.ImagePanorama(six);
  panorama7 = new PANOLENS.ImagePanorama(seven);

  // Link spheres
  panorama1.link(panorama2, new THREE.Vector3(-207.5, 504.88, -6000.0), 600, `${process.env.PUBLIC_URL + 'next.png'}`);
  // Edgecase for bug in panolens for second image useage, has to do with caching on their end.
  panorama2.link(panorama3, new THREE.Vector3(-6907.5, 304.88, 1000.0), 600, `${process.env.PUBLIC_URL + 'next.png'}`);
  panorama2.link(panorama3, new THREE.Vector3(-6907.5, 304.88, 1000.0), 600, `${process.env.PUBLIC_URL + 'next.png'}`);
  panorama3.link(panorama4, new THREE.Vector3(-207.5, 504.88, -6000.0), 600, `${process.env.PUBLIC_URL + 'next.png'}`);
  panorama4.link(panorama5, new THREE.Vector3(-207.5, 504.88, -6000.0), 600, `${process.env.PUBLIC_URL + 'next.png'}`);
  panorama5.link(panorama6, new THREE.Vector3(-207.5, 504.88, -6000.0), 600, `${process.env.PUBLIC_URL + 'next.png'}`);
  panorama6.link(panorama7, new THREE.Vector3(-207.5, 504.88, -6000.0), 600, `${process.env.PUBLIC_URL + 'next.png'}`);
  panorama7.link(panorama1, new THREE.Vector3(-207.5, 504.88, -6000.0), 600, `${process.env.PUBLIC_URL + 'next.png'}`);

  // Sphere one artwork - contains one
  const geometryOne = new THREE.PlaneGeometry(300, 300, 300);
  const loaderOne = new THREE.TextureLoader();
  const materialOne = new THREE.MeshBasicMaterial({
    map: loaderOne.load(imagePlaceholder),
    side: THREE.DoubleSide,
  });
  const planeOne = new THREE.Mesh(geometryOne, materialOne);
  planeOne.position.set(-30, 100, 340);
  panorama1.add(planeOne);










  panorama1.addEventListener("progress", onProgress);
  panorama1.addEventListener("load", onLoad);
  panorama1.addEventListener("enter", onEnter);
  panorama1.add(
    sphereOneArt,
    valveInfospot,
    seatInfospot,
    topboxInfospot,
    endingInfospot
  );

  // Viewer
  viewer = new PANOLENS.Viewer({
    controlBar: false,
    initialLookAt: new THREE.Vector3(0, 0, 5000),
  });
  viewer.OrbitControls.enabled = false;
  viewer.add(panorama1, panorama2, panorama3, panorama4, panorama5, panorama6, panorama7);

  return (
    <div>
      <div className="wrap">
        <div id="dialog" className="type-wrap">
          <span id="typed" ref={spanEl} />
        </div>
      </div>
      <ReactHowler
        src="https://memorypearl.s3.us-east-2.amazonaws.com/269570__vonora__cuckoo-the-nightingale-duet.mp3"
        playing={true}
        loop={true}
      />
    </div>
  );
}
